import React, { useState } from 'react';

function LapAdvisor() {
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [laptops, setLaptops] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault(); // Form gönderimini engelle

    setLoading(true);
    setError('');

    // API isteği: /top_laptops?minPrice=xxx&maxPrice=yyy
    fetch(`http://localhost:5000/top_laptops?minPrice=${minPrice}&maxPrice=${maxPrice}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Sunucu hatası');
        }
        return res.json();
      })
      .then((data) => {
        setLaptops(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>LapAdvisor AI</h1>
      <p>LapAdvisor AI, E-Ticaret sitelerinde satılan bütün laptoplara gelen kullanıcı yorumlarını analiz eder ve size kullanıcılar tarafından en çok beğenilen laptopları önerir.</p>

      <form onSubmit={handleSubmit} style={styles.form}>
        <label>
          Min Fiyat:
          <input
            type="number"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            style={styles.input}
          />
        </label>

        <label>
          Max Fiyat:
          <input
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            style={styles.input}
          />
        </label>

        <button type="submit" style={styles.button}>
          Filtrele
        </button>
      </form>

      {loading && <p>Yükleniyor...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Sonuçları listeleme */}
      <div style={styles.results}>
        {laptops.map((item, index) => (
          <div key={index} style={styles.card}>
            <h3>{item.laptop_name}</h3>
            <img src={item.image} alt={item.laptop_name} style={{ width: '150px' }} />
            <p>Fiyat: {item.price} TL</p>
            <p>{item.summary}</p>
            <a href={item.link} target="_blank" rel="noreferrer">Satıcıya Git</a>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '1rem 2rem'
  },
  title: {
    fontSize: '1.5rem',
    marginBottom: '1rem'
  },
  form: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '1rem'
  },
  input: {
    marginLeft: '0.5rem',
    padding: '0.3rem'
  },
  button: {
    padding: '0.5rem 1rem',
    cursor: 'pointer'
  },
  results: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
    gap: '1rem'
  },
  card: {
    border: '1px solid #ccc',
    padding: '1rem',
    borderRadius: '4px'
  }
};

export default LapAdvisor;
