import React, { useState } from 'react';

function Chatbot() {
  const [message, setMessage] = useState('');
  const [conversation, setConversation] = useState([]);
  const [error, setError] = useState('');

  // Tek parça, tam handleSend fonksiyonu
  const handleSend = async () => {
    // Boş mesajı göndermeyelim
    if (!message.trim()) return;

    // Kullanıcı mesajını önce ekrana ekleyelim
    setConversation((prev) => [
      ...prev,
      { sender: 'user', text: message }
    ]);

    try {
      // Flask'taki /chat endpoint'ine POST isteği
      const response = await fetch('http://localhost:5000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ message })
      });

      // Yanıt başarısızsa hata fırlat
      if (!response.ok) {
        throw new Error('Sunucu hatası');
      }

      // JSON'u parse et
      const data = await response.json();

      // Bot cevabını ekrana ekle
      if (data.response) {
        setConversation((prev) => [
          ...prev,
          { sender: 'bot', text: data.response }
        ]);
      } else {
        // Beklenmeyen durum
        setConversation((prev) => [
          ...prev,
          { sender: 'bot', text: 'Bir hata oluştu veya yanıt yok.' }
        ]);
      }
    } catch (err) {
      console.error(err);
      setError(err.message);
      // Sohbete de hata mesajı ekleyebilirsiniz
      setConversation((prev) => [
        ...prev,
        { sender: 'bot', text: 'Bir hata oluştu.' }
      ]);
    }

    // Metin kutusunu temizle
    setMessage('');
  };

  // Devamında render kısmı
  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <h2>Chatbot</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div style={{ border: '1px solid #ccc', padding: '1rem', minHeight: '200px' }}>
        {conversation.map((msg, index) => (
          <p key={index} style={{ margin: '0.5rem 0' }}>
            <strong>{msg.sender === 'user' ? 'You' : 'Bot'}:</strong> {msg.text}
          </p>
        ))}
      </div>

      <div style={{ display: 'flex', marginTop: '1rem' }}>
        <input
          style={{ flex: 1, marginRight: '0.5rem' }}
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Mesajınızı yazın..."
        />
        <button onClick={handleSend} style={{ padding: '0.5rem 1rem' }}>
          Gönder
        </button>
      </div>
    </div>
  );
}

export default Chatbot;
