import React, { useState, useEffect } from 'react';

function LaptopList() {
  const [laptops, setLaptops] = useState([]);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState('');     // Arama metni
  const [error, setError] = useState('');     // Hata mesajı

  // Sayfa ilk açıldığında tüm laptopları çekiyoruz
  useEffect(() => {
    fetch('http://localhost:5000/get_all_laptops')
      .then(response => response.json())
      .then(data => {
        // API yapınıza göre "data" veya "data.data" gelebilir.
        // Aşağıdaki örnekte "data" içinde "data" dizisi varsa:
        if (data.data) {
          setLaptops(data.data);
        } else if (Array.isArray(data)) {
          // Eğer doğrudan dizi dönüyorsa
          setLaptops(data);
        } else {
          setLaptops([]);
        }
        setLoading(false);
      })
      .catch(error => {
        console.error('API hatası:', error);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  // Arama butonuna tıklanınca çalışacak fonksiyon
  const handleSearch = (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Eğer arama kutusu boşsa tekrar tüm laptopları çek
    if (!query.trim()) {
      fetch('http://localhost:5000/get_all_laptops')
        .then(res => res.json())
        .then(data => {
          if (data.data) {
            setLaptops(data.data);
          } else if (Array.isArray(data)) {
            setLaptops(data);
          } else {
            setLaptops([]);
          }
          setLoading(false);
        })
        .catch(err => {
          console.error('API hatası:', err);
          setError(err.message);
          setLoading(false);
        });
      return;
    }

    // Arama kutusu doluysa /laptops_by_name endpoint'ine istek at
    fetch(`http://localhost:5000/laptops_by_name?query=${query}`)
      .then(res => res.json())
      .then(data => {
        setLaptops(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('API hatası:', err);
        setError(err.message);
        setLoading(false);
      });
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div>
      <div style={styles.content}>
        <h1 style={styles.title}>Hoş Geldiniz</h1>
        <p style={styles.paragraph}>
          Burada en popüler laptopları bulabilir ve inceleyebilirsiniz.
        </p>
      </div>

      {/* Arama Formu */}
      <div style={{ textAlign: 'center', marginBottom: '1rem' }}>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form onSubmit={handleSearch}>
          <input
            type='text'
            placeholder='Laptop adı veya marka ara...'
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            style={{ padding: '0.5rem' }}
          />
          <button type='submit' style={{ marginLeft: '0.5rem', padding: '0.5rem 1rem' }}>
            Ara
          </button>
        </form>
      </div>

      <h2 style={{ textAlign: 'center' }}>Tüm Laptoplar</h2>
      <ul style={styles.gridContainer}>
        {laptops.map((item, index) => (
          <li key={index} style={styles.gridItem}>
            <h3>{item.laptop_name}</h3>
            <img src={item.image} alt={item.laptop_name} width="250" />
            <p style={styles.priceStyle}>Fiyat: {item.price} TL</p>
            <div style={styles.reviewContainer}>
              <h4 style={styles.reviewTitle}>Kullanıcı Değerlendirmeleri</h4>
              <p style={styles.reviewText}>{item.summary}</p>
            </div>
            <button style={styles.button}>
              <a
                href={item.link}
                target="_blank"
                rel="noreferrer"
                style={styles.link}
              >
                Satıcı Sayfasına Git
              </a>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  gridContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gap: '16px',
    listStyleType: 'none',
    padding: 0,
    margin: 0
  },
  gridItem: {
    border: '1px solid #ccc',
    padding: '16px',
    backgroundColor: '#fff',
    textAlign: 'center'
  },
  priceStyle: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '1.2em'
  },
  reviewContainer: {
    margin: '1.5 rem 0',
    padding: '1rem',
    backgroundColor: '#f8f8f8',
    borderRadius: '5px'
  },
  reviewTitle: {
    fontSize: '1.1rem',
    fontWeight: 'bold',
    marginBottom: '0.5rem'
  },
  reviewText: {
    fontSize: '0.95rem',
    lineHeight: '1.4',
    color: '#333'
  },
  button: {
    backgroundColor: '#424141',
    border: 'none',
    borderRadius: '4px',
    padding: '0.8rem 1.2rem',
    cursor: 'pointer',
    marginTop: '1rem'
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: 'bold'
  },
  content: {
    padding: '1rem 2rem'
  },
  title: {
    fontSize: '2rem',
    marginBottom: '1rem'
  },
  paragraph: {
    fontSize: '1.1rem',
    marginBottom: '2rem'
  }
};

export default LaptopList;
