import React, { useState, useEffect } from 'react';

function LaptopList() {
  const [laptops, setLaptops] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/get_all_laptops')
      .then(response => response.json())
      .then(data => {
        setLaptops(data.data); // API cevabında "data": [ {laptop1}, {laptop2}, ... ]
        setLoading(false);
      })
      .catch(error => {
        console.error('API hatası:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div>
      <h2 style = {{textAlign: 'center'}}>Tüm Laptoplar</h2>
      {/* Grid için inline stil */}
      <ul style={styles.gridContainer}>
        {laptops.map((item, index) => (
          <li key={index} style={styles.gridItem}>
            <h3>{item.laptop_name}</h3>
            <img src={item.image} alt={item.laptop_name} width="250" />
            <p style={styles.priceStyle}>Fiyat: {item.price} TL</p>
            <h4>Kullanıcı Değerlendirmeleri</h4>
            <p>{item.summary}</p>
            <a 
  href={item.link} 
  target="_blank" 
  rel="noreferrer"
  style={{
    display: 'inline-block',
    backgroundColor: '#ddddd',  // Buton rengi
    color: '#000',               // Yazı rengi
    padding: '8px 16px',
    borderRadius: '4px',
    textDecoration: 'none',
    border: 'none'               // Bazı tarayıcılarda link etrafında outline olabilir
  }}
>
  Satıcı Sayfasına Git
</a>

          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  gridContainer: {
    /* Grid yapısı */
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)', // 4 sütun
    gap: '16px', // kartlar arası boşluk
    listStyleType: 'none', // ul/li default stilini kaldırmak isterseniz
    padding: 0,
    margin: 0
  },
  gridItem: {
    border: '1px solid #ccc',
    padding: '16px',
    backgroundColor: '#fff',
    textAlign: 'center'
  },
  priceStyle: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '1.2em'

  }
};

export default LaptopList;
