import React, { useState } from 'react';

function Chatbot() {
  const [message, setMessage] = useState('');
  const [conversation, setConversation] = useState([]);

  const handleSend = async () => {
    // Boş mesajı yoksay
    if (!message.trim()) return;

    // Kullanıcı mesajını ekle
    setConversation((prev) => [...prev, { sender: 'user', text: message }]);

    try {
      // Flask'taki /chat endpoint'ine POST isteği
      const response = await fetch('http://localhost:5000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message })
      });

      // HTTP 200 değilse "Sunucu hatası" diyebiliriz (istersen bu mesajı bile kaldır)
      if (!response.ok) {
        // throw new Error('Sunucu hatası');
        // istersen hiç hata vermeyip return; diyebilirsin
        return;
      }

      // Gelen JSON'u parse et
      const data = await response.json();
      // data örn: { "error": "..."} veya { "response": "..."} veya { "message": "...", "data": [...] }

      // Eğer "error" varsa, sadece error içeriğini gösterelim
      if (data.error) {
        setConversation((prev) => [...prev, { sender: 'bot', text: data.error }]);
      }
      // Eğer "response" varsa, onu gösterelim
      else if (data.response) {
        setConversation((prev) => [...prev, { sender: 'bot', text: data.response }]);
      }
      // Eğer "message" varsa (farklı formata göre)
      else if (data.message) {
        setConversation((prev) => [...prev, { sender: 'bot', text: data.message }]);
      }
      // Hiçbiri yoksa, istersen hiç mesaj ekleme
      else {
        // Do nothing or show empty
        // setConversation((prev) => [...prev, { sender: 'bot', text: '' }]);
      }
    } catch (err) {
      console.error(err);
      // Burada da "hata" göstermemek istersen, boş bırakabilirsin
      // setConversation((prev) => [...prev, { sender: 'bot', text: 'Sunucu hatası.' }]);
    }

    // Mesaj kutusunu temizle
    setMessage('');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Chatbot</h2>

      <div style={styles.chatWindow}>
        {conversation.map((msg, index) => (
          <div key={index} style={styles.messageRow}>
            <strong>{msg.sender === 'user' ? 'Sen' : 'Bot'}:</strong> {msg.text}
          </div>
        ))}
      </div>

      <div style={styles.inputArea}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Mesaj yaz..."
          style={styles.input}
        />
        <button onClick={handleSend} style={styles.sendButton}>
          Gönder
        </button>
      </div>
    </div>
  );
}

// Basit stiller
const styles = {
  container: {
    maxWidth: '600px',
    margin: '2rem auto',
    fontFamily: 'sans-serif',
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '1rem'
  },
  title: {
    textAlign: 'center'
  },
  chatWindow: {
    border: '1px solid #ccc',
    borderRadius: '4px',
    minHeight: '300px',
    padding: '1rem',
    marginBottom: '1rem',
    overflowY: 'auto'
  },
  messageRow: {
    marginBottom: '0.5rem'
  },
  inputArea: {
    display: 'flex',
    gap: '0.5rem'
  },
  input: {
    flex: 1,
    padding: '0.5rem'
  },
  sendButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#ff3e3e',
    border: 'none',
    borderRadius: '4px',
    color: '#fff',
    cursor: 'pointer'
  }
};

export default Chatbot;
