import React, { useState, useEffect } from 'react';

function LaptopList() {
  const [laptops, setLaptops] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/get_all_laptops')
      .then(response => response.json())
      .then(data => {
        setLaptops(data.data); // API cevabında "data": [ {laptop1}, {laptop2}, ... ]
        setLoading(false);
      })
      .catch(error => {
        console.error('API hatası:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div>
      <h2>Tüm Laptoplar</h2>
      <ul>
        {laptops.map((item, index) => (
          <li key={index}>
            <h3>{item.laptop_name}</h3>
            <img src={item.image} alt={item.laptop_name} width="150" />
            <p>Fiyat: {item.price} TL</p>
            <p>{item.summary}</p>
            <a href={item.link} target="_blank" rel="noreferrer">Detay</a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LaptopList;
