import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Chatbot() {
  const [message, setMessage] = useState('');
  const [conversation, setConversation] = useState([]);

  // Mesajı /chat endpoint'ine gönderip cevabı alıyoruz
  const handleSend = async () => {
    if (!message.trim()) return; // Boş mesajı yoksay
    const data = await response.json();

    // Kullanıcının mesajını önce ekranda gösterelim
    if (data.data && data.data.length > 0) {
        // Önerilen ürünler var
        // Mesaj olarak text formatında ekleyebilirsiniz
        // veya "structured" bir şekilde ekrana basabilirsiniz
        setConversation((prev) => [
          ...prev,
          {
            sender: 'bot',
            text: data.message, // "Önerilen ürünler"
            recommendations: data.data // array of laptops
          }
        ]);
      } else {
        // data.data boş olabilir
        setConversation((prev) => [
          ...prev,
          { sender: 'bot', text: data.message, recommendations: [] }
        ]);
    }

    // Mesaj kutusunu temizle
    setMessage('');
  };

  // Enter'a basınca da mesajı göndermek isterseniz handleKeyDown gibi ekleyebilirsiniz
  // Şimdilik butonla gönderiyoruz

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Chatbot</h2>

      {/* Mesajların göründüğü alan */}
      <div style={styles.chatWindow}>
      {conversation.map((msg, i) => (
        <div key={i}>
            <p><strong>{msg.sender}:</strong> {msg.text}</p>
            {msg.recommendations && msg.recommendations.length > 0 && (
            <ul>
                {msg.recommendations.map((r, idx) => (
                <li key={idx}>
                    <strong>{r.laptop_name}</strong> - Fiyat: {r.price} TL
                    <br />
                    <a href={r.link} target="_blank" rel="noreferrer">Satıcıya Git</a>
                </li>
                ))}
            </ul>
            )}
        </div>
        ))}
      </div>

      {/* Mesaj yazma alanı */}
      <div style={styles.inputArea}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Mesajınızı yazın..."
          style={styles.input}
        />
        <button onClick={handleSend} style={styles.sendButton}>
          Gönder
        </button>
      </div>
    </div>
  );
}

// Basit stiller
const styles = {
  container: {
    maxWidth: '600px',
    margin: '2rem auto',
    fontFamily: 'sans-serif',
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '1rem'
  },
  title: {
    textAlign: 'center'
  },
  chatWindow: {
    border: '1px solid #ccc',
    borderRadius: '4px',
    minHeight: '300px',
    padding: '1rem',
    marginBottom: '1rem',
    overflowY: 'auto'
  },
  messageRow: {
    marginBottom: '0.5rem'
  },
  inputArea: {
    display: 'flex',
    gap: '0.5rem'
  },
  input: {
    flex: 1,
    padding: '0.5rem'
  },
  sendButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#ff3e3e',
    border: 'none',
    borderRadius: '4px',
    color: '#fff',
    cursor: 'pointer'
  }
};

export default Chatbot;
