import React, { useState, useEffect } from 'react';

function LaptopList() {
  const [laptops, setLaptops] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/get_all_laptops')
      .then(response => response.json())
      .then(data => {
        setLaptops(data.data); // API cevabında "data": [ {laptop1}, {laptop2}, ... ]
        setLoading(false);
      })
      .catch(error => {
        console.error('API hatası:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  return (
    
    <div>
      <div style={styles.content}>
        <h1 style={styles.title}>Hoş Geldiniz</h1>
        <p style={styles.paragraph}>
          Burada en popüler laptopları bulabilir ve inceleyebilirsiniz.
        </p>
      </div>
      <h2 style = {{textAlign: 'center'}}>Tüm Laptoplar</h2>
      {/* Grid için inline stil */}
      <ul style={styles.gridContainer}>
        {laptops.map((item, index) => (
          <li key={index} style={styles.gridItem}>
            <h3>{item.laptop_name}</h3>
            <img src={item.image} alt={item.laptop_name} width="250" />
            <p style={styles.priceStyle}>Fiyat: {item.price} TL</p>
          <div style={styles.reviewContainer}>
            <h4 style={styles.reviewTitle}>Kullanıcı Değerlendirmeleri</h4>
            <p style={styles.reviewText}>{item.summary}</p>
          </div>
          <button style={styles.button}>
            <a 
              href={item.link} 
              target="_blank" 
              rel="noreferrer"
              style={styles.link}
            >
              Satıcı Sayfasına Git
            </a>
          </button>

          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  gridContainer: {
    /* Grid yapısı */
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)', // 4 sütun
    gap: '16px', // kartlar arası boşluk
    listStyleType: 'none', // ul/li default stilini kaldırmak isterseniz
    padding: 0,
    margin: 0
  },
  gridItem: {
    border: '1px solid #ccc',
    padding: '16px',
    backgroundColor: '#fff',
    textAlign: 'center'
  },
  priceStyle: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '1.2em'

  },
  reviewContainer: {
    margin: '1.5 rem 0',
    padding: '1rem',
    backgroundColor: '#f8f8f8',
    borderRadius: '5px'
  },
  reviewTitle: {
    fontSize: '1.1rem',
    fontWeight: 'bold',
    marginBottom: '0.5rem'  
  },
  reviewText: {
    fontSize: '0.95rem',
    lineHeight: '1.4',
    color: '#333'
  },
  button: {
    backgroundColor: '#ff3e3e',
    border: 'none',
    borderRadius: '4px',
    padding: '0.8rem 1.2rem',
    cursor: 'pointer',
    marginTop: '1rem'
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: 'bold'
  }
};

export default LaptopList;
