import React, { useState, useEffect } from 'react';

function LaptopList() {
  const [laptops, setLaptops] = useState([]);
  const [page, setPage] = useState(1);       // Mevcut sayfa numarası
  const limit = 25;                         // Sayfa başına 25 kayıt
  const [total, setTotal] = useState(0);     // Toplam kayıt sayısı
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Sayfa veya limit değiştiğinde API'den veriyi çek
  useEffect(() => {
    fetchLaptops();
  }, [page]);

  const fetchLaptops = () => {
    setLoading(true);
    setError('');

    fetch(`http://localhost:5000/get_all_laptops?page=${page}&limit=${limit}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Sunucu hatası');
        }
        return res.json();
      })
      .then((data) => {
        // data içinden "data" dizisi, "page", "limit", "total" bilgilerini alalım
        setLaptops(data.data || []);
        setTotal(data.total || 0);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  // Sonraki sayfa
  const handleNextPage = () => {
    // Toplam sayfa sayısı = Math.ceil(total / limit)
    const maxPage = Math.ceil(total / limit);
    if (page < maxPage) {
      setPage(page + 1);
    }
  };

  // Önceki sayfa
  const handlePrevPage = () => {
    if (page > 1) {
      setPage(page - 1);
    }
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div>
      <h2 style={{ textAlign: 'center' }}>Laptop Listesi (Sayfa: {page})</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <ul style={styles.gridContainer}>
        {laptops.map((item, index) => (
          <li key={index} style={styles.gridItem}>
            <h3>{item.laptop_name}</h3>
            <img src={item.image} alt={item.laptop_name} width="250" />
            <p style={styles.priceStyle}>Fiyat: {item.price} TL</p>
            <div style={styles.reviewContainer}>
              <h4 style={styles.reviewTitle}>Kullanıcı Değerlendirmeleri</h4>
              <p style={styles.reviewText}>{item.summary}</p>
            </div>
            <button style={styles.button}>
              <a
                href={item.link}
                target="_blank"
                rel="noreferrer"
                style={styles.link}
              >
                Satıcı Sayfasına Git
              </a>
            </button>
          </li>
        ))}
      </ul>

      {/* Sayfalama Butonları */}
      <div style={{ textAlign: 'center', margin: '1rem' }}>
        <button onClick={handlePrevPage} disabled={page <= 1}>
          Önceki
        </button>
        <span style={{ margin: '0 1rem' }}>
          Sayfa {page} / {Math.ceil(total / limit)}
        </span>
        <button
          onClick={handleNextPage}
          disabled={page >= Math.ceil(total / limit)}
        >
          Sonraki
        </button>
      </div>
    </div>
  );
}

const styles = {
  gridContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gap: '16px',
    listStyleType: 'none',
    padding: 0,
    margin: 0
  },
  gridItem: {
    border: '1px solid #ccc',
    padding: '16px',
    backgroundColor: '#fff',
    textAlign: 'center'
  },
  priceStyle: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '1.2em'
  },
  reviewContainer: {
    margin: '1.5 rem 0',
    padding: '1rem',
    backgroundColor: '#f8f8f8',
    borderRadius: '5px'
  },
  reviewTitle: {
    fontSize: '1.1rem',
    fontWeight: 'bold',
    marginBottom: '0.5rem'
  },
  reviewText: {
    fontSize: '0.95rem',
    lineHeight: '1.4',
    color: '#333'
  },
  button: {
    backgroundColor: '#424141',
    border: 'none',
    borderRadius: '4px',
    padding: '0.8rem 1.2rem',
    cursor: 'pointer',
    marginTop: '1rem'
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: 'bold'
  }
};

export default LaptopList;
