import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Chatbot() {
  const [message, setMessage] = useState('');
  const [conversation, setConversation] = useState([]);

  // Mesajı /chat endpoint'ine gönderip cevabı alıyoruz
  const handleSend = async () => {
    if (!message.trim()) return; // Boş mesajı yoksay

    // Kullanıcının mesajını önce ekranda gösterelim
    setConversation((prev) => [
      ...prev,
      { sender: 'user', text: message }
    ]);

    try {
      const response = await fetch('http://localhost:5000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ message })
      });

      if (!response.ok) {
        throw new Error('Sunucu hatası');
      }

      const data = await response.json();
      if (data.response) {
        // Bot cevabını ekleyelim
        setConversation((prev) => [
          ...prev,
          { sender: 'bot', text: data.response }
        ]);
      }
    } catch (error) {
      console.error(error);
      // İsteğin hata alması durumunda isterseniz conversation’a hata mesajı ekleyebilirsiniz
      setConversation((prev) => [
        ...prev,
        { sender: 'bot', text: 'Bir hata oluştu.' }
      ]);
    }

    // Mesaj kutusunu temizle
    setMessage('');
  };

  // Enter'a basınca da mesajı göndermek isterseniz handleKeyDown gibi ekleyebilirsiniz
  // Şimdilik butonla gönderiyoruz

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Chatbot</h2>

      {/* Mesajların göründüğü alan */}
      <div style={styles.chatWindow}>
        {conversation.map((msg, index) => (
          <div key={index} style={styles.messageRow}>
            <strong>{msg.sender === 'user' ? 'You' : 'Bot'}:</strong> {msg.text}
          </div>
        ))}
      </div>

      {/* Mesaj yazma alanı */}
      <div style={styles.inputArea}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Mesajınızı yazın..."
          style={styles.input}
        />
        <button onClick={handleSend} style={styles.sendButton}>
          Gönder
        </button>
      </div>
    </div>
  );
}

// Basit stiller
const styles = {
  container: {
    maxWidth: '600px',
    margin: '2rem auto',
    fontFamily: 'sans-serif',
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '1rem'
  },
  title: {
    textAlign: 'center'
  },
  chatWindow: {
    border: '1px solid #ccc',
    borderRadius: '4px',
    minHeight: '300px',
    padding: '1rem',
    marginBottom: '1rem',
    overflowY: 'auto'
  },
  messageRow: {
    marginBottom: '0.5rem'
  },
  inputArea: {
    display: 'flex',
    gap: '0.5rem'
  },
  input: {
    flex: 1,
    padding: '0.5rem'
  },
  sendButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#ff3e3e',
    border: 'none',
    borderRadius: '4px',
    color: '#fff',
    cursor: 'pointer'
  }
};

export default Chatbot;
