import React, { useState, useEffect } from 'react';
import FilterLaptops from './FilterLaptops';

function LaptopList() {
  const [laptops, setLaptops] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [query, setQuery] = useState('');

  
  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  const fetchAllLaptops = () => {
    setLoading(true);
    setError('');
    fetch('http://localhost:5000/get_all_laptops')
      .then((res) => {
        if (!res.ok) {
          throw new Error('Sunucu hatası (get_all_laptops)');
        }
        return res.json();
      })
      .then((data) => {
        // API yapısı gereği "data": [ {...}, {...} ] gibi gelebilir
        // ya da "data" içinde "data" varsa ona göre ayarlayın
        // Bu örnekte "data" dizisi döndüğünü varsayıyoruz
        if (data.data) {
          // eğer endpoint "data" içinde tutuyorsa
          setLaptops(data.data);
        } else if (Array.isArray(data)) {
          // eğer direkt dizi dönüyorsa
          setLaptops(data);
        } else {
          setLaptops([]);
        }
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchAllLaptops();
  }, []);


  // 2) İsim arama fonksiyonu
  const handleSearch = (e) => {
    e.preventDefault();
    // Eğer arama kutusu boşsa tekrar tüm laptopları çek
    if (!query.trim()) {
      fetchAllLaptops();
      return;
    }

    setLoading(true);
    setError('');
    fetch(`http://localhost:5000/laptops_by_name?query=${query}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Sunucu hatası (laptops_by_name)');
        }
        return res.json();
      })
      .then((data) => {
        // Endpoint doğrudan dizi döndürüyor
        setLaptops(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };


  return (
    
    <div>
      <div style={styles.content}>
        <h1 style={styles.title}>Hoş Geldiniz</h1>
        <p style={styles.paragraph}>
          Burada en popüler laptopları bulabilir ve inceleyebilirsiniz.
        </p>
      </div>
      
      <h2 style = {{textAlign: 'center'}}>Tüm Laptoplar</h2>
      {/* Arama Formu */}
      <form onSubmit={handleSearch} style={styles.searchForm}>
        <input
          type='text'
          placeholder='Laptop adı veya marka ara...'
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={styles.searchInput}
        />
        <button type='submit' style={styles.searchButton}>Ara</button>
      </form>

      {loading && <p>Yükleniyor...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <ul style={styles.gridContainer}>
        {laptops.map((item, index) => (
          <li key={index} style={styles.gridItem}>
            <h3>{item.laptop_name}</h3>
            <img src={item.image} alt={item.laptop_name} width="250" />
            <p style={styles.priceStyle}>Fiyat: {item.price} TL</p>
          <div style={styles.reviewContainer}>
            <h4 style={styles.reviewTitle}>Kullanıcı Değerlendirmeleri</h4>
            <p style={styles.reviewText}>{item.summary}</p>
          </div>
          <button style={styles.button}>
            <a 
              href={item.link} 
              target="_blank" 
              rel="noreferrer"
              style={styles.link}
            >
              Satıcı Sayfasına Git
            </a>
          </button>

          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  gridContainer: {
    /* Grid yapısı */
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)', // 4 sütun
    gap: '16px', // kartlar arası boşluk
    listStyleType: 'none', // ul/li default stilini kaldırmak isterseniz
    padding: 0,
    margin: 0
  },
  gridItem: {
    border: '1px solid #ccc',
    padding: '16px',
    backgroundColor: '#fff',
    textAlign: 'center'
  },
  priceStyle: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '1.2em'

  },
  reviewContainer: {
    margin: '1.5 rem 0',
    padding: '1rem',
    backgroundColor: '#f8f8f8',
    borderRadius: '5px'
  },
  reviewTitle: {
    fontSize: '1.1rem',
    fontWeight: 'bold',
    marginBottom: '0.5rem'  
  },
  reviewText: {
    fontSize: '0.95rem',
    lineHeight: '1.4',
    color: '#333'
  },
  button: {
    backgroundColor: '#ff3e3e',
    border: 'none',
    borderRadius: '4px',
    padding: '0.8rem 1.2rem',
    cursor: 'pointer',
    marginTop: '1rem'
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: 'bold'
  },
  content: {
    padding: '1rem 2rem'
  },
  title: {
    fontSize: '2rem',
    marginBottom: '1rem'
  },
  paragraph: {
    fontSize: '1.1rem',
    marginBottom: '2rem'
  }
};

export default LaptopList;
