import React, { useState, useEffect } from 'react';

export default function HomePage() {
  const [featured, setFeatured] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchFeaturedLaptops();
  }, []);

  const fetchFeaturedLaptops = () => {
    setLoading(true);
    setError('');

    fetch('http://localhost:5000/most_reviewed_laptops')
      .then(res => {
        if (!res.ok) {
          throw new Error('Sunucu hatası (most_reviewed_laptops)');
        }
        return res.json();
      })
      .then(data => {
        setFeatured(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Hata:', err);
        setError(err.message);
        setLoading(false);
      });
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Öne Çıkan Laptoplar</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div style={styles.featuredContainer}>
        {featured.map((lap, idx) => (
          <div key={idx} style={styles.laptopCard}>
            <h3>{lap.laptop_name}</h3>
            <p>Toplam Yorum: {lap.total_reviews}</p>
            {lap.image && (
              <img
                src={lap.image}
                alt={lap.laptop_name}
                style={styles.image}
              />
            )}
            <p style={styles.price}>Fiyat: {lap.price} TL</p>
            <p style={styles.summary}>{lap.summary}</p>
            <a
              href={lap.link}
              target="_blank"
              rel="noreferrer"
              style={styles.linkButton}
            >
              Satıcıya Git
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '1000px',
    margin: '0 auto',
    padding: '1rem'
  },
  title: {
    textAlign: 'center',
    marginBottom: '1rem'
  },
  featuredContainer: {
    display: 'flex',
    gap: '1rem',
    flexWrap: 'wrap',
    justifyContent: 'center'
  },
  laptopCard: {
    width: '220px',
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '1rem',
    textAlign: 'center',
    backgroundColor: '#fff',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
  },
  image: {
    width: '100%',
    height: 'auto',
    margin: '0.5rem 0'
  },
  price: {
    color: 'red',
    fontWeight: 'bold'
  },
  summary: {
    fontSize: '0.9rem'
  },
  linkButton: {
    display: 'inline-block',
    marginTop: '0.5rem',
    backgroundColor: '#ff3e3e',
    color: '#fff',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    textDecoration: 'none'
  }
};
