import React, { useState } from 'react';

function LapAdvisor() {
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [laptops, setLaptops] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    fetch(`http://localhost:5000/top_laptops?minPrice=${minPrice}&maxPrice=${maxPrice}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Sunucu hatası');
        }
        return res.json();
      })
      .then((data) => {
        setLaptops(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>LapAdvisor AI</h1>
      <p>
        LapAdvisor AI, E-Ticaret sitelerinde satılan bütün laptoplara gelen kullanıcı yorumlarını analiz eder
        ve size kullanıcılar tarafından en çok beğenilen laptopları önerir.
      </p>

      <form onSubmit={handleSubmit} style={styles.form}>
        <label>
          Min Fiyat:
          <input
            type="number"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            style={styles.input}
          />
        </label>

        <label>
          Max Fiyat:
          <input
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            style={styles.input}
          />
        </label>

        <button type="submit" style={styles.button}>
          Filtrele
        </button>
      </form>

      {loading && <p>Yükleniyor...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div style={styles.results}>
        {laptops.map((item, index) => (
          <div key={index} style={styles.gridItem}>
            <h3>{item.laptop_name}</h3>
            <img
              src={item.image}
              alt={item.laptop_name}
              style={styles.image}
            />
            <p style={styles.priceStyle}>Fiyat: {item.price} TL</p>
            <p>{item.summary}</p>
            <button style={styles.button2}>
              <a
                href={item.link}
                target="_blank"
                rel="noreferrer"
                style={styles.link}
              >
                Satıcı Sayfasına Git
              </a>
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '1rem 2rem'
  },
  title: {
    fontSize: '1.5rem',
    marginBottom: '1rem'
  },
  form: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '1rem'
  },
  input: {
    marginLeft: '0.5rem',
    padding: '0.3rem'
  },
  button: {
    padding: '0.5rem 1rem',
    cursor: 'pointer'
  },
  results: {
    display: 'grid',
    // 1) Grid ayarını auto-fill + minmax ile değiştirdik
    gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
    gap: '12px',
    listStyleType: 'none',
    padding: 0,
    margin: 0
  },
  gridItem: {
    border: '1px solid #ccc',
    padding: '16px',
    backgroundColor: '#fff',
    textAlign: 'center'
  },
  // 2) Resim boyutu: width %100, maxWidth 200px
  image: {
    width: '100%',
    maxWidth: '200px',
    height: 'auto'
  },
  priceStyle: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '1.2em'
  },
  reviewContainer: {
    margin: '1.5 rem 0',
    padding: '1rem',
    backgroundColor: '#f8f8f8',
    borderRadius: '5px'
  },
  reviewTitle: {
    fontSize: '1.1rem',
    fontWeight: 'bold',
    marginBottom: '0.5rem'
  },
  reviewText: {
    fontSize: '0.95rem',
    lineHeight: '1.4',
    color: '#333'
  },
  button2: {
    backgroundColor: '#ff3e3e',
    border: 'none',
    borderRadius: '4px',
    padding: '0.8rem 1.2rem',
    cursor: 'pointer',
    marginTop: '1rem'
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: 'bold'
  }
};

export default LapAdvisor;
